package chapter03;

public class exam_8 {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int a = 10;
		int b = 20;
		int c = 30;
		
		System.out.println(a < b); //a는 b보다 작다.
		System.out.println(a > b); //a는 b보다 크다.
		System.out.println(a + b <= c); //a+b는 c보다 작거나 같다.
		System.out.println(a + b >= c); //a+b는 c보다 크거나 같다.
	}

}
